import { memo, useMemo, useState } from 'react';
import clickSound from './ClickSound.m4a';

function Calculator({ workouts, allowSound }) {
  const [number, setNumber] = useState(workouts.at(0).numExercises);
  const [sets, setSets] = useState(3);
  const [speed, setSpeed] = useState(90);
  const [durationBreak, setDurationBreak] = useState(5);

  /*
    Why do we use 'useMemo' here?
      The duration, mins, and seconds variables are recalculated every time Calculator renders, which includes every time a user changes a value in the form. 
      To fix this, you can use useMemo to memoize these variables. 
      This way, they will only be recalculated when number, sets, speed, or durationBreak changes.
      This change will prevent the section from re-rendering every time a user changes a value in the form.

  // Derived state
  const duration = (number * sets * speed) / 60 + (sets - 1) * durationBreak;
  const mins = Math.floor(duration);
  const seconds = (duration - mins) * 60;
  */

  const { duration, mins, seconds } = useMemo(() => {
    const duration = (number * sets * speed) / 60 + (sets - 1) * durationBreak;
    const mins = Math.floor(duration);
    const seconds = Math.round((duration - mins) * 60);
    return { duration, mins, seconds };
  }, [number, sets, speed, durationBreak]);


  function playSound() {
    if (!allowSound) return;
    const sound = new Audio(clickSound);
    sound.play();
  };

  function handleNumberChange(e) {
    setNumber(+e.target.value);
    playSound();
  }

  function handleSetsChange(e) {
    setSets(+e.target.value);
    playSound();
  }

  function handleSpeedChange(e) {
    setSpeed(+e.target.value);
    playSound();
  }

  function handleDurationBreakChange(e) {
    setDurationBreak(+e.target.value);
    playSound();
  }

  return (
    <>
      <form>
        <div>
          <label>Type of workout</label>
          <select value={number} onChange={handleNumberChange}>
            {workouts.map((workout) => (
              <option value={workout.numExercises} key={workout.name}>
                {workout.name} ({workout.numExercises} exercises)
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>How many sets?</label>
          <input
            type='range'
            min='1'
            max='5'
            value={sets}
            onChange={handleSetsChange}
          />
          <span>{sets}</span>
        </div>
        <div>
          <label>How fast are you?</label>
          <input
            type='range'
            min='30'
            max='180'
            step='30'
            value={speed}
            onChange={handleSpeedChange}
          />
          <span>{speed} sec/exercise</span>
        </div>
        <div>
          <label>Break length</label>
          <input
            type='range'
            min='1'
            max='10'
            value={durationBreak}
            onChange={handleDurationBreakChange}
          />
          <span>{durationBreak} minutes/break</span>
        </div>
      </form>
      <section>
        <button onClick={() => {}}>â€“</button>
        <p>
          {mins < 10 && '0'}
          {mins}:{seconds < 10 && '0'}
          {seconds}
        </p>
        <button onClick={() => {}}>+</button>
      </section>
    </>
  );
}

/* 
  If it's some children components that are re-rendering, just because the parent component is re-rednering
*/

export default memo(Calculator);
